mapper.update:
update o2o.tb_goods_info
goods_name = #{goodsName}, goods_desc = #{description}, goods_image_url = #{imageURL}, goods_normal_price = #{normalPrice}, goods_promotion_price = #{promotionPrice}, goods_priority = #{priority}, update_time = #{updateTime}, status = #{status}, goods_category_id = #{category.goodsCategoryId},
goods_id = #{goodsId} and goods_shop_id = #{shopInfo.shopId}

mapper.select:
select tginfo.*, tgc.goods_category_id, tgc.goods_category_name, tsi.shop_id, tsi.shop_name from o2o.tb_goods_info as tginfo left join o2o.tb_goods_category as tgc on tginfo.goods_category_id = tgc.goods_category_id inner join o2o.tb_shop_info as tsi on tginfo.goods_shop_id = tsi.shop_id
and tsi.shop_id = #{condition.shopInfo.shopId}
and tsi.shop_name like '%${condition.shopInfo.shopName}%'
and goods_name like '%${condition.goodsName}%'
and tginfo.status = #{condition.status}
and tgc.goods_category_id = #{condition.category.goodsCategoryId}
and tgc.goods_category_name like '%${condition.category.goodsCategoryName}%'
order by tginfo.goods_priority desc limit #{start}, #{pageSize}

select count(1) from o2o.tb_goods_info as tginfo left join o2o.tb_goods_category as tgc on tginfo.goods_category_id = tgc.goods_category_id inner join o2o.tb_shop_info as tsi on tginfo.goods_shop_id = tsi.shop_id
and tsi.shop_id = #{condition.shopInfo.shopId}
and tsi.shop_name like '%${condition.shopInfo.shopName}%'
and goods_name like '%${condition.goodsName}%'
and tginfo.status = #{condition.status}
and tgc.goods_category_id = #{condition.category.goodsCategoryId}
and tgc.goods_category_name like '%${condition.category.goodsCategoryName}%'
